                            placeholder="zip code">
                    </input>
                    <button type="submit">Submit</button>
                
            <div className="radio">
            {/* <div key={item.id} onChange={(i)=>this.callOptions(i)}> */}
           <label>
            {/* <input type="radio"  checked={this.state.checkedRadio == i} ref={(el) => this["myRadioRef" + i] = el} value={item.id} onChange={this.changeRadio} />{item.nm} /> */}
            <input type="radio" value="option2" />
            Actual Temp
          </label>

          <label>
            <input type="radio" value="option2" />
            Real Feel
          </label>
            </div>
          
          </form>
         
        </div> 
      </div>
    );
  }
}
 
export default WeatherApp;
